exports.error = function(msg){
	var chalk = require('chalk');
	var output = chalk.red;
	console.log(output(msg));
};

exports.info = function(msg){
	var chalk = require('chalk');
	var output = chalk.blue.bold;
	console.log(output(msg));
};

exports.success = function(msg){
	var chalk = require('chalk');
	var output = chalk.green.bold;
	console.log(output(msg));
};